// frontend/src/pages/Menu.js

import { useEffect, useState } from "react";
import { getProducts } from "../services/api";

export default function Menu({ onAddToCart }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts()
      .then(res => setProducts(res.data))
      .catch(err => console.error("Eroare la încărcare produse:", err));
  }, []);

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
      {products.map(p => (
        <div
          key={p.ProductId}
          style={{
            border: "1px solid #ccc",
            borderRadius: 4,
            padding: "10px",
            width: "200px",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between"
          }}
        >
          <div>
            <h3 style={{ margin: "0 0 8px" }}>{p.Name}</h3>
            <p style={{ margin: "0 0 8px", fontSize: "0.9em" }}>{p.Description}</p>
            <p style={{ margin: "0 0 12px", fontWeight: "bold" }}>{p.Price} lei</p>
          </div>
          <button
            onClick={() =>
              onAddToCart({
                productId:    p.ProductId,
                Name:         p.Name,
                description:  p.Description,
                quantity:     1,
                pricePerUnit: p.Price
              })
            }
            disabled={!p.IsAvailable}
            style={{
              padding: "8px",
              borderRadius: 4,
              cursor: p.IsAvailable ? "pointer" : "not-allowed",
              backgroundColor: p.IsAvailable ? "#007bff" : "#ccc",
              color: "#fff",
              border: "none"
            }}
          >
            {p.IsAvailable ? "Adaugă în coș" : "Indisponibil"}
          </button>
        </div>
      ))}
    </div>
  );
}
