import React, { useState } from "react";
import { login, register } from "../services/api";
import "./LoginRegister.css";

export default function LoginRegister({ onLogin }) {
  const [tab, setTab] = useState("login"); // "login" sau "register"
  const [email, setEmail] = useState("");
  const [pw, setPw] = useState("");
  const [error, setError] = useState("");
  const [info, setInfo] = useState("");

  const handleLogin = async e => {
    e.preventDefault();
    setError(""); setInfo("");
    try {
      const { data } = await login({ email, password: pw });
      localStorage.setItem("token", data.token);
      onLogin(data.user);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.response?.data?.msg ||
        "Eroare necunoscută"
      );
    }
  };

  const handleRegister = async e => {
    e.preventDefault();
    setError(""); setInfo("");
    try {
      await register({ email, password: pw });
      setInfo("Înregistrare realizată! Poți face login.");
      setTab("login");
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.response?.data?.msg ||
        "Eroare necunoscută"
      );
    }
  };

  return (
    <div className="single-card-wrapper">
      <div className="form-tab-card">
        <div className="logo-box">
          {/* În public/, pune `orderly-logo.png` */}
          <img src="/orderly-logo.png" alt="Orderly" />
        </div>

        <div className="tabs">
          <button
            className={tab === "login" ? "active" : ""}
            onClick={() => { setTab("login"); setError(""); setInfo(""); }}
          >
            Login
          </button>
          <button
            className={tab === "register" ? "active" : ""}
            onClick={() => { setTab("register"); setError(""); setInfo(""); }}
          >
            Register
          </button>
        </div>

        {(error || info) && (
          <p className={error ? "error-msg" : "info-msg"}>
            {error || info}
          </p>
        )}

        {tab === "login" ? (
          <form onSubmit={handleLogin} className="form-card">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Parolă"
              value={pw}
              onChange={e => setPw(e.target.value)}
              required
            />
            <button type="submit">Login</button>
          </form>
        ) : (
          <form onSubmit={handleRegister} className="form-card">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Parolă"
              value={pw}
              onChange={e => setPw(e.target.value)}
              required
            />
            <button type="submit">Register</button>
          </form>
        )}
      </div>
    </div>
  );
}
