// frontend/src/App.js
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
  Navigate
} from "react-router-dom";

import Login from "./pages/Login";
import Register from "./pages/Register";
import Menu from "./pages/Menu";
import Orders from "./pages/Orders";
import "./LoginRegister.css";

function App() {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);
  const [tab, setTab] = useState("login");

  // Încarcă user și coș din localStorage
  useEffect(() => {
    const rawUser = localStorage.getItem("user");
    if (rawUser && rawUser !== "undefined") {
      try { setUser(JSON.parse(rawUser)); }
      catch { localStorage.removeItem("user"); }
    }
    const rawCart = localStorage.getItem("cart");
    if (rawCart && rawCart !== "undefined") {
      try { setCart(JSON.parse(rawCart)); }
      catch { localStorage.removeItem("cart"); }
    }
  }, []);

  // Autentificare / deautentificare
  const handleLogin = u => {
    setUser(u);
    localStorage.setItem("user", JSON.stringify(u));
  };
  const handleLogout = () => {
    setUser(null);
    setCart([]);
    localStorage.removeItem("user");
    localStorage.removeItem("cart");
  };

  // Gestionare coș
  const handleAddToCart = product => {
    const next = [...cart, product];
    setCart(next);
    localStorage.setItem("cart", JSON.stringify(next));
  };
  const handleSetCart = newCart => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  // Dacă nu e logat, afișăm login/register
  if (!user) {
    return (
      <div className="single-card-wrapper">
        <div className="logo-box">
          <h1 style={{
            fontSize: 32, margin: 0, color: "#333", fontWeight: 700,
            textShadow: "0 1px 1px rgba(0,0,0,0.1)"
          }}>Orderly</h1>
        </div>
        <div className="form-tab-card">
          <div className="tabs">
            <button
              className={tab === "login" ? "active" : ""}
              onClick={() => setTab("login")}
            >Login</button>
            <button
              className={tab === "register" ? "active" : ""}
              onClick={() => setTab("register")}
            >Register</button>
          </div>
          {tab === "login"
            ? <Login onLogin={handleLogin} />
            : <Register />
          }
        </div>
      </div>
    );
  }

  // Dacă e logat, înfășurăm în <Router>
  return (
    <Router>
      {/* Navbar fix sus */}
      <nav style={{
        position: "fixed", top: 0, left: 0, right: 0,
        background: "#fff", padding: "10px 20px",
        boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
        display: "flex", justifyContent: "center", gap: 10, zIndex: 1000
      }}>
        <NavLink
          to="/menu"
          style={({ isActive }) => ({
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: isActive ? "#007bff" : "#ccc",
            color: "#fff",
            textDecoration: "none"
          })}
        >Meniu</NavLink>

        <NavLink
          to="/cart"
          style={({ isActive }) => ({
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: isActive ? "#007bff" : "#ccc",
            color: "#fff",
            textDecoration: "none"
          })}
        >Coș</NavLink>

        <NavLink
          to="/orders"
          style={({ isActive }) => ({
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: isActive ? "#007bff" : "#ccc",
            color: "#fff",
            textDecoration: "none"
          })}
        >Comenzi</NavLink>

        <button
          onClick={handleLogout}
          style={{
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: "#e63946",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >Logout</button>
      </nav>

      {/* Spațiu pentru navbar */}
      <div style={{ paddingTop: 70, padding: 20 }}>
        <h1 style={{ textAlign: "center", margin: "20px 0" }}>
          Bine ai venit, {user.name}
        </h1>

        <Routes>
          {/* Ruta default redirecționează la /menu */}
          <Route path="/" element={<Navigate to="/menu" replace />} />

          <Route
            path="/menu"
            element={<Menu onAddToCart={handleAddToCart} />}
          />

          <Route
            path="/cart"
            element={
              cart.length === 0
                ? <p>Coșul este gol.</p>
                : (
                  <ul>
                    {cart.map((it, i) => (
                      <li key={i}>{it.name} x{it.quantity}</li>
                    ))}
                  </ul>
                )
            }
          />

          <Route
            path="/orders"
            element={
              <Orders
                cart={cart}
                setCart={handleSetCart}
                user={user}
              />
            }
          />

          {/* 404 simplu */}
          <Route path="*" element={<p>Pagină inexistentă</p>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
