// frontend/src/pages/CourierOrders.js
import React, { useEffect, useState } from "react";
import { getAllOrders, updateOrderStatus } from "../services/api";

export default function CourierOrders() {
  const [orders, setOrders] = useState([]);

  const fetchOrders = async () => {
    try {
      const res = await getAllOrders();
      setOrders(res.data);
    } catch (err) {
      console.error("Eroare la preluarea comenzilor pentru curier:", err);
    }
  };

  useEffect(() => {
    fetchOrders();
    // Optional polling:
    // const id = setInterval(fetchOrders, 5000);
    // return () => clearInterval(id);
  }, []);

  const nextStatus = status => {
    switch (status) {
      case "pending":   return "accepted";
      case "accepted":  return "preparing";
      case "preparing": return "finished";
      default:          return null;
    }
  };

  const handleAdvance = async orderId => {
    const ord = orders.find(o => o.OrderId === orderId);
    const ns = nextStatus(ord.Status);
    if (!ns) return;
    try {
      await updateOrderStatus(orderId, ns);
      fetchOrders();
    } catch (err) {
      console.error("Eroare la actualizarea statusului:", err);
    }
  };

  return (
    <div style={{ padding:20, maxWidth:800, margin:"0 auto" }}>
      <h2 style={{ textAlign:"center" }}>Panou Curier</h2>
      {orders.filter(o=>["pending","accepted","preparing"].includes(o.Status)).length===0 ? (
        <p style={{ textAlign:"center" }}>Nu există comenzi.</p>
      ) : (
        orders
          .filter(o=>["pending","accepted","preparing"].includes(o.Status))
          .map(o=>(
            <div key={o.OrderId} style={{
              border:"1px solid #ccc",
              borderRadius:4,
              padding:12,
              marginBottom:12
            }}>
              <p><strong>ID:</strong> {o.OrderId}</p>
              <p><strong>Status:</strong> {o.Status}</p>
              <p>
                <strong>Items:</strong>{" "}
                {o.items.map(i=>`${i.productId}×${i.Quantity}`).join(", ")}
              </p>
              <button
                onClick={()=>handleAdvance(o.OrderId)}
                style={{
                  padding:"6px 12px",
                  borderRadius:4,
                  background:"#007bff",
                  color:"#fff",
                  border:"none",
                  cursor:"pointer",
                  marginTop:8
                }}
              >
                {o.Status==="pending"   && "Acceptă comanda"}
                {o.Status==="accepted"  && "Începe pregătirea"}
                {o.Status==="preparing" && "Finalizează comanda"}
              </button>
            </div>
          ))
      )}
    </div>
  );
}
