// frontend/src/App.js
import { useState, useEffect, useRef } from "react";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Menu from "./pages/Menu";
import Orders from "./pages/Orders";
import "./LoginRegister.css";

function App() {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);
  const [tab, setTab] = useState("login");

  // refs pentru scroll
  const menuRef    = useRef(null);
  const cosRef     = useRef(null);
  const comenziRef = useRef(null);

  // la mount, încarcă user și coș din localStorage
  useEffect(() => {
    const rawUser = localStorage.getItem("user");
    if (rawUser && rawUser !== "undefined") {
      try {
        setUser(JSON.parse(rawUser));
      } catch {
        localStorage.removeItem("user");
      }
    }

    const rawCart = localStorage.getItem("cart");
    if (rawCart && rawCart !== "undefined") {
      try {
        setCart(JSON.parse(rawCart));
      } catch {
        localStorage.removeItem("cart");
      }
    }
  }, []);

  // login / logout
  const handleLogin = (u) => {
    setUser(u);
    localStorage.setItem("user", JSON.stringify(u));
  };
  const handleLogout = () => {
    setUser(null);
    setCart([]);
    localStorage.removeItem("user");
    localStorage.removeItem("cart");
  };

  // adăugare produs în coș
  const handleAddToCart = (product) => {
    const next = [...cart, product];
    setCart(next);
    localStorage.setItem("cart", JSON.stringify(next));
  };
  const handleSetCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  // scroll către o secțiune
  const scrollTo = (ref) => {
    if (ref.current) {
      ref.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

  // dacă nu e logat, arată login/register
  if (!user) {
    return (
      <div className="single-card-wrapper">
        <div className="logo-box">
          <h1 style={{
            fontSize: "32px",
            margin: 0,
            color: "#333",
            fontWeight: "700",
            textShadow: "0 1px 1px rgba(0,0,0,0.1)"
          }}>
            Orderly
          </h1>
        </div>

        <div className="form-tab-card">
          <div className="tabs">
            <button
              className={tab === "login" ? "active" : ""}
              onClick={() => setTab("login")}
            >
              Login
            </button>
            <button
              className={tab === "register" ? "active" : ""}
              onClick={() => setTab("register")}
            >
              Register
            </button>
          </div>

          {tab === "login"
            ? <Login onLogin={handleLogin} />
            : <Register />
          }
        </div>
      </div>
    );
  }

  // dacă e logat, arată dashboard-ul cu navbar + secțiuni
  return (
    <div>
      {/* NAVBAR fix în partea de sus */}
      <nav
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          background: "#fff",
          padding: "10px 20px",
          boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
          display: "flex",
          justifyContent: "center",
          gap: "10px",
          zIndex: 1000
        }}
      >
        <button onClick={() => scrollTo(menuRef)}>Meniu</button>
        <button onClick={() => scrollTo(cosRef)}>Coș</button>
        <button onClick={() => scrollTo(comenziRef)}>Comenzi</button>
        <button onClick={handleLogout}>Logout</button>
      </nav>

      {/* wrapper cu padding pentru a nu fi ascuns conținutul de navbar */}
      <div style={{ paddingTop: 70 }}>
        {/* Titlu centrat */}
        <h1 style={{ textAlign: "center", margin: "20px 0" }}>
          Bine ai venit, {user.name}
        </h1>

        {/* Secțiunea Meniu */}
        <section
          id="menu"
          ref={menuRef}
          style={{ padding: "40px 20px", marginBottom: 40 }}
        >
          <h2>Meniu</h2>
          <Menu onAddToCart={handleAddToCart} />
        </section>

        {/* Secțiunea Coș */}
        <section
          id="cos"
          ref={cosRef}
          style={{ padding: "40px 20px", marginBottom: 40 }}
        >
          <h2>Coș</h2>
          {cart.length === 0 ? (
            <p>Coșul este gol.</p>
          ) : (
            <ul>
              {cart.map((it, i) => (
                <li key={i}>
                  {it.name} x{it.quantity}
                </li>
              ))}
            </ul>
          )}
        </section>

        {/* Secțiunea Comenzi */}
        <section
          id="comenzi"
          ref={comenziRef}
          style={{ padding: "40px 20px", marginBottom: 40 }}
        >
          <h2>Comenzi existente</h2>
          <Orders cart={cart} setCart={handleSetCart} user={user} />
        </section>
      </div>
    </div>
  );
}

export default App;
