import React, { useEffect, useState } from "react";
import { getAllOrders, updateOrderStatus } from "../services/api";

export default function CourierOrders({ onLogout }) {
  const [orders, setOrders] = useState([]);

  // Fetch all orders
  const fetchOrders = async () => {
    try {
      const res = await getAllOrders();
      setOrders(res.data);
    } catch (err) {
      console.error("Eroare la preluarea comenzilor pentru curier:", err);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  // Determine next status, matching DB values (case-insensitive)
  const nextStatus = status => {
    const s = status.toLowerCase();
    if (s === "pending") return "accepted";
    if (s === "accepted") return "preparing";
    if (s === "preparing") return "finished";
    return null;
  };

  // Advance the order to the next status
  const handleAdvance = async orderId => {
    const ord = orders.find(o => o.OrderId === orderId);
    console.log("Advance clicked for order", orderId, "current status", ord.Status);
    const ns = nextStatus(ord.Status);
    if (!ns) {
      console.warn("Nu există status următor pentru:", ord.Status);
      return;
    }
    try {
      console.log(`Updating order ${orderId} to status ${ns}`);
      await updateOrderStatus(orderId, ns);
      fetchOrders();
    } catch (err) {
      console.error("Eroare la actualizarea statusului:", err);
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 800, margin: "0 auto" }}>
      {/* Logout button */}
      <div style={{ display: "flex", justifyContent: "flex-end", marginBottom: 20 }}>
        <button
          onClick={onLogout}
          style={{
            padding: "6px 12px",
            borderRadius: 4,
            background: "#e63946",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >
          Logout
        </button>
      </div>

      <h2 style={{ textAlign: "center" }}>Panou Curier</h2>

      {orders.filter(o => ["pending", "accepted", "preparing"].includes(o.Status.toLowerCase())).length === 0 ? (
        <p style={{ textAlign: "center" }}>Nu există comenzi.</p>
      ) : (
        orders
          .filter(o => ["pending", "accepted", "preparing"].includes(o.Status.toLowerCase()))
          .map(o => (
            <div
              key={o.OrderId}
              style={{
                border: "1px solid #ccc",
                borderRadius: 4,
                padding: 12,
                marginBottom: 12
              }}
            >
              <p><strong>ID:</strong> {o.OrderId}</p>
              <p><strong>Status:</strong> {o.Status}</p>
              <p>
                <strong>Items:</strong>{" "}
                {o.items.map(i => `${i.productId}×${i.Quantity}`).join(", ")}
              </p>
              <button
                onClick={() => handleAdvance(o.OrderId)}
                style={{
                  padding: "6px 12px",
                  borderRadius: 4,
                  background: "#007bff",
                  color: "#fff",
                  border: "none",
                  cursor: "pointer",
                  marginTop: 8
                }}
              >
                {o.Status.toLowerCase() === "pending" && "Acceptă comanda"}
                {o.Status.toLowerCase() === "accepted" && "Începe pregătirea"}
                {o.Status.toLowerCase() === "preparing" && "Finalizează comanda"}
              </button>
            </div>
          ))
      )}
    </div>
  );
}
