// frontend/src/App.js
import React, { useState, useEffect } from "react";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Menu from "./pages/Menu";
import Orders from "./pages/Orders";
import "./LoginRegister.css";

export default function App() {
  const [user, setUser]   = useState(null);
  const [cart, setCart]   = useState([]);
  const [tab, setTab]     = useState("login");
  const [view, setView]   = useState("menu"); // 'menu' | 'cart' | 'orders'

  // Încarcă user și coș din localStorage la mount
  useEffect(() => {
    const rawUser = localStorage.getItem("user");
    if (rawUser && rawUser !== "undefined") {
      try { setUser(JSON.parse(rawUser)); }
      catch { localStorage.removeItem("user"); }
    }
    const rawCart = localStorage.getItem("cart");
    if (rawCart && rawCart !== "undefined") {
      try { setCart(JSON.parse(rawCart)); }
      catch { localStorage.removeItem("cart"); }
    }
  }, []);

  // Handlere Login / Logout
  const handleLogin = u => {
    setUser(u);
    localStorage.setItem("user", JSON.stringify(u));
  };
  const handleLogout = () => {
    setUser(null);
    setCart([]);
    localStorage.removeItem("user");
    localStorage.removeItem("cart");
  };

  // Handlere coș
  const handleAddToCart = item => {
    const next = [...cart, item];
    setCart(next);
    localStorage.setItem("cart", JSON.stringify(next));
  };
  const handleSetCart = newCart => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  // Dacă n-ai user => Login/Register
  if (!user) {
    return (
      <div className="single-card-wrapper">
        <div className="logo-box">
          <h1 style={{
            fontSize: "32px",
            margin: 0,
            color: "#333",
            fontWeight: 700,
            textShadow: "0 1px 1px rgba(0,0,0,0.1)"
          }}>
            Orderly
          </h1>
        </div>
        <div className="form-tab-card">
          <div className="tabs">
            <button
              className={tab === "login" ? "active" : ""}
              onClick={() => setTab("login")}
            >Login</button>
            <button
              className={tab === "register" ? "active" : ""}
              onClick={() => setTab("register")}
            >Register</button>
          </div>
          {tab === "login"
            ? <Login onLogin={handleLogin} />
            : <Register />
          }
        </div>
      </div>
    );
  }

  // Dacă ai user => Dashboard cu trei “pagini”
  return (
    <div style={{ padding: 20 }}>
      {/* Header */}
      <header style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: 20
      }}>
        <h1 style={{ textAlign: "center", flex: 1 }}>
          Bine ai venit, {user.name}
        </h1>
        <button
          onClick={handleLogout}
          style={{
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: "#e63946",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >
          Logout
        </button>
      </header>

      {/* Navbar */}
      <nav style={{
        display: "flex",
        justifyContent: "center",
        gap: "10px",
        marginBottom: 20
      }}>
        <button
          onClick={() => setView("menu")}
          style={{
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: view === "menu" ? "#007bff" : "#ccc",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >
          Meniu
        </button>
        <button
          onClick={() => setView("cart")}
          style={{
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: view === "cart" ? "#007bff" : "#ccc",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >
          Coș
        </button>
        <button
          onClick={() => setView("orders")}
          style={{
            padding: "8px 16px",
            borderRadius: 4,
            backgroundColor: view === "orders" ? "#007bff" : "#ccc",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >
          Comenzi
        </button>
      </nav>

      {/* Conținut vizibil în funcție de view */}
      {view === "menu" && (
        <section>
          <h2>Meniu</h2>
          <Menu onAddToCart={handleAddToCart} />
        </section>
      )}

      {view === "cart" && (
        <section>
          <h2>Coș</h2>
          {cart.length === 0 ? (
            <p>Coșul este gol.</p>
          ) : (
            <ul>
              {cart.map((it,i) => (
                <li key={i} style={{ marginBottom: 8 }}>
                  {it.name} x{it.quantity}
                </li>
              ))}
            </ul>
          )}
        </section>
      )}

      {view === "orders" && (
        <section>
          <h2>Comenzi existente</h2>
          <Orders cart={cart} setCart={handleSetCart} user={user} />
        </section>
      )}
    </div>
  );
}
