import React, { useEffect, useState } from "react";
import {
  getOrdersByUser,
  createOrder,
  deleteOrder
} from "../services/api";

export default function Orders({ cart, setCart, user }) {
  const [orders, setOrders] = useState([]);
  const [showCart, setShowCart] = useState(true);

  // Încarcă comenzile existente ale utilizatorului
  const fetchOrders = async () => {
    try {
      const res = await getOrdersByUser(user.id);
      setOrders(res.data);
    } catch (err) {
      console.error("Eroare la preluarea comenzilor:", err);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, [user.id]);

  // Handlere pentru butoane
  const handleShowCart = () => setShowCart(true);
  const handleShowActiveOrders = () => {
    fetchOrders();        // reîncarcă comenzile înainte de afișare
    setShowCart(false);
  };

  // Șterge un produs din coș înainte de plasare
  const removeFromCart = index => setCart(prev => prev.filter((_, i) => i !== index));

  // Trimite o nouă comandă
  const checkout = async () => {
    if (cart.length === 0) return;
    try {
      const payload = {
        userId:          user.id,
        deliveryAddress: user.address,
        items:           cart.map(item => ({
          productId:    item.productId,
          quantity:     item.quantity,
          pricePerUnit: item.pricePerUnit
        }))
      };

      await createOrder(payload);
      setCart([]);      // golim coșul după comandă
      fetchOrders();
    } catch (err) {
      console.error("Eroare la crearea comenzii:", err);
    }
  };

  // Șterge o comandă existentă
  const removeOrder = async orderId => {
    try {
      await deleteOrder(orderId);
      setOrders(prev => prev.filter(o => o.OrderId !== orderId));
    } catch (err) {
      console.error("Eroare la ștergerea comenzii:", err);
    }
  };

  // Filtrează comenzile active (exclude cele finalizate/cancelate)
  const activeOrders = orders.filter(o => o.Status !== "Delivered" && o.Status !== "Cancelled");

  // Stil butoane de navigare
  const navBtnStyle = active => ({
    marginRight: 8,
    padding: "8px 16px",
    borderRadius: 4,
    border: "none",
    cursor: "pointer",
    backgroundColor: active ? "#007bff" : "#ccc",
    color: "#fff"
  });

  return (
    <div style={{ padding: 20 }}>
      <div style={{ marginBottom: 20 }}>
        <button onClick={handleShowCart} style={navBtnStyle(showCart)}>
          Coș
        </button>
        <button onClick={handleShowActiveOrders} style={navBtnStyle(!showCart)}>
          Comenzi active
        </button>
      </div>

      {showCart ? (
        <>
          <h3>Coș</h3>
          {cart.length === 0 ? (
            <p>Coșul este gol.</p>
          ) : (
            <ul>
              {cart.map((it, i) => (
                <li key={i} style={{ marginBottom: 8 }}>
                  {it.name} x{it.quantity}{" "}
                  <button onClick={() => removeFromCart(i)}>
                    Șterge produs
                  </button>
                </li>
              ))}
            </ul>
          )}
          <button onClick={checkout} disabled={cart.length === 0} style={{ marginTop: 10 }}>
            Trimite comandă
          </button>
        </>
      ) : (
        <>
          <h3>Comenzi active</h3>
          {activeOrders.length === 0 ? (
            <p>Nu ai comenzi active.</p>
          ) : (
            activeOrders.map(o => (
              <div
                key={o.OrderId}
                style={{
                  border: "1px solid #ddd",
                  borderRadius: 4,
                  margin: "10px 0",
                  padding: 10
                }}
              >
                <p>
                  <strong>ID:</strong> {o.OrderId} | <strong>Status:</strong> {o.Status} | <strong>Total:</strong> {o.TotalPrice} lei
                </p>
                <p><strong>Livrare la:</strong> {o.DeliveryAddress}</p>
                <p><strong>Items:</strong></p>
                <ul>
                  {o.items.map(it => (
                    <li key={it.ItemId}>
                      Produs #{it.ProductId} x{it.Quantity}
                    </li>
                  ))}
                </ul>
                <button onClick={() => removeOrder(o.OrderId)}>
                  Șterge comanda
                </button>
              </div>
            ))
          )}
        </>
      )}
    </div>
  );
}
